mapper.select:
SELECT id, title, content, writer_id, like_count, created_at, updated_at
FROM board
WHERE id = #{id}

SELECT b.id, b.title, b.content, b.writer_id, b.like_count, b.created_at, b.updated_at
FROM board b
WHERE b.id = #{boardId}

{CALL sp_board_cud(
    #{operation, mode=IN, jdbcType=CHAR},
    #{id, mode=IN, jdbcType=BIGINT},
    #{title, mode=IN, jdbcType=VARCHAR},
    #{content, mode=IN, jdbcType=LONGVARCHAR},
    #{writerId, mode=IN, jdbcType=BIGINT},
    #{likeCount, mode=IN, jdbcType=INTEGER},
    #{resultMessage, mode=OUT, jdbcType=VARCHAR},
    #{affectedRows, mode=OUT, jdbcType=INTEGER}
)}
