mapper.select:
SELECT id, member_id, coupon_id, created_at, updated_at
FROM member_coupon
WHERE member_id = #{memberId}

mapper.delete:
DELETE FROM member_coupon
WHERE member_id = #{memberId} AND coupon_id IN

    #{couponId}

mapper.select:
{CALL sp_member_coupon_cud(
    #{operation, mode=IN, jdbcType=CHAR},
    #{id, mode=IN, jdbcType=BIGINT},
    #{memberId, mode=IN, jdbcType=BIGINT},
    #{couponId, mode=IN, jdbcType=BIGINT},
    #{resultMessage, mode=OUT, jdbcType=VARCHAR},
    #{affectedRows, mode=OUT, jdbcType=INTEGER}
)}
