mapper.select:
SELECT id, email, password, nickname, member_role, created_at, updated_at
FROM member
WHERE id = #{id}

SELECT id, email, password, nickname, member_role, created_at, updated_at
FROM member
WHERE email = #{email}

SELECT id, email, password, nickname, member_role, created_at, updated_at
FROM member
WHERE nickname = #{nickname}

SELECT COUNT(*) > 0
FROM member
WHERE email = #{email}
