mapper.select:
SELECT id, email, password, nickname, member_role, created_at, updated_at
FROM member
WHERE id = #{id}

SELECT id, email, password, nickname, member_role, created_at, updated_at
FROM member
WHERE email = #{email}

SELECT id, email, password, nickname, member_role, created_at, updated_at
FROM member
WHERE nickname = #{nickname}

SELECT COUNT(*) > 0
FROM member
WHERE email = #{email}

{CALL sp_member_cud(
    #{operation, mode=IN, jdbcType=CHAR},
    #{id, mode=IN, jdbcType=BIGINT},
    #{email, mode=IN, jdbcType=VARCHAR},
    #{password, mode=IN, jdbcType=VARCHAR},
    #{nickname, mode=IN, jdbcType=VARCHAR},
    #{memberRole, mode=IN, jdbcType=VARCHAR},
    #{resultMessage, mode=OUT, jdbcType=VARCHAR},
    #{affectedRows, mode=OUT, jdbcType=INTEGER}
)}
