mapper.select:
SELECT id, buyer_id, seller_id, product_id, product_origin_price, product_discount_price, using_coupon_ids, created_at, updated_at
FROM trade_history
WHERE buyer_id = #{buyerId}

SELECT id, buyer_id, seller_id, product_id, product_origin_price, product_discount_price, using_coupon_ids, created_at, updated_at
FROM trade_history
WHERE seller_id = #{sellerId}
