mapper.select:
{CALL sp_mail_storage_cud(
    #{operation, mode=IN, jdbcType=CHAR},
    #{id, mode=IN, jdbcType=BIGINT},
    #{memberId, mode=IN, jdbcType=BIGINT},
    #{email, mode=IN, jdbcType=VARCHAR},
    #{nickname, mode=IN, jdbcType=VARCHAR},
    #{mailStatus, mode=IN, jdbcType=VARCHAR},
    #{resultMessage, mode=OUT, jdbcType=VARCHAR},
    #{affectedRows, mode=OUT, jdbcType=INTEGER}
)}

SELECT id, member_id, email, nickname, mail_status, created_at, updated_at
FROM mail_storage
WHERE id = #{id}

SELECT id, member_id, email, nickname, mail_status, created_at, updated_at
FROM v_mail_storage_by_status
WHERE mail_status = #{mailStatus}
