mapper.select:
{CALL sp_schedule_task_cud(
    #{operation, mode=IN, jdbcType=CHAR},
    #{taskId, mode=IN, jdbcType=VARCHAR},
    #{executionTime, mode=IN, jdbcType=TIMESTAMP},
    #{status, mode=IN, jdbcType=VARCHAR},
    #{resultMessage, mode=OUT, jdbcType=VARCHAR},
    #{affectedRows, mode=OUT, jdbcType=INTEGER}
)}

SELECT task_id, execution_time, status
FROM schedule_task
WHERE task_id = #{taskId}
