mapper.select:
SELECT id, product_id, buyer_id, seller_id, chatting_status, created_at, updated_at
FROM chatting_room
WHERE id = #{id}

SELECT id, product_id, buyer_id, seller_id, chatting_status, created_at, updated_at
FROM chatting_room
WHERE seller_id = #{sellerId} AND buyer_id = #{buyerId} AND product_id = #{productId}

SELECT id, product_id, buyer_id, seller_id, chatting_status, created_at, updated_at
FROM chatting_room
WHERE product_id = #{productId}
